import styled from "styled-components";

import pageHeading from "./assets/images/page-heading.jpeg";
import pageHeadingPattern from "./assets/images/page-heading-pattern.png";
import logoLg from "./assets/images/logo.png";
import player from "./assets/images/tyronethompson.png";

import {
  Radar,
  RadarChart,
  Legend,
  PolarGrid,
  PolarAngleAxis,
  PolarRadiusAxis,
  ResponsiveContainer,
} from "recharts";

import CircularStat from "./CircularStat";
import PlayerDetail from "./PlayerDetails";
import PlayerStats from "./PlayerStats";

const StyledPlayerHeading = styled.div`
  background-color: #1e2024;
  background-image: url(${pageHeading});
  background-size: cover;
  background-repeat: no-repeat;
  background-position: 50% 50%;
  position: relative;

  &:before {
    height: 24px;
    background-image: linear-gradient(
        90deg,
        rgba(236, 240, 246, 0.15) 4px,
        transparent 0
      ),
      linear-gradient(90deg, rgba(236, 240, 246, 0.15) 4px, transparent 0),
      linear-gradient(90deg, rgba(236, 240, 246, 0.15) 4px, transparent 0),
      linear-gradient(90deg, rgba(236, 240, 246, 0.15) 4px, transparent 0),
      linear-gradient(90deg, rgba(236, 240, 246, 0.15) 4px, transparent 0),
      linear-gradient(90deg, rgba(236, 240, 246, 0.15) 4px, transparent 0),
      linear-gradient(90deg, rgba(0, 0, 0, 0.1) 4px, transparent 0),
      linear-gradient(90deg, rgba(0, 0, 0, 0.11) 4px, transparent 0);
    background-repeat: repeat-x;
    background-size: 36px 12px, 36px 15px, 36px 17px, 36px 20px, 36px 18px,
      36px 15px, 72px 15px, 108px 18px;
    background-position: 0 100%, 6px 100%, 12px 100%, 18px 100%, 24px 100%,
      30px 100%, 6px 100%, 24px 100%;
  }
  &:after,
  &:before {
    content: "";
    display: block;
    position: absolute;
    left: 0;
    right: 0;
    bottom: 0;
  }

  &:after {
    top: 0;
    z-index: 1;
    background-image: url(${pageHeadingPattern});
    background-size: auto;
    background-position: 100% 0;
    background-repeat: no-repeat;
    opacity: 0.4;
  }
  .container {
    position: relative;
    z-index: 2;
  }
`;

const TeamLogo = styled.div`
  position: absolute;
  left: -45%;
  top: -5%;
  opacity: 0.05;
  z-index: -1;

  @media (min-width: 992px) {
    left: -10%;
    opacity: 0.1;
  }
`;

const PlayerInfo = styled.div`
  color: #fff;
  position: relative;
  width: 100%;

  @media (min-width: 992px) {
    display: flex;
  }
`;

const PlayerPhoto = styled.figure`
  display: block;
  margin: 0;
`;

const PlayerInfoItemCard = styled.div`
  position: relative;
  display: block;
  overflow: hidden;
  margin: 0;
  padding: 0;
  transform: translate3d(0, 0, 0);
`;

const PlayerInfoItemCardContent = styled.div`
  position: relative;
  top: 0;
  left: 0;
  display: block;
  opacity: 1;
  width: 15000px;
  transform: translate3d(0px, 0px, 0px);
  &:before,
  &:after {
    display: table;
    content: "";
  }
  &:after {
    clear: both;
  }
`;

const PlayerInfoItem = styled.div`
  display: block;
  float: left;
  height: 100%;
  min-height: 1px;
  outline: none;

  &.photo {
    @media (max-width: 991px) {
      img {
        margin: 0 auto;
      }
    }

    @media (min-width: 992px) {
      -ms-flex-preferred-size: 33%;
      flex-basis: 33%;
      width: 312px;
      margin: 0;
    }

    @media (min-width: 1200px) {
      width: 400px;
    }
  }

  &.details {
    width: 400px;
    @media (min-width: 992px) {
      -ms-flex-preferred-size: 38%;
      flex-basis: 38%;
      padding: 48px 0;
      width: 352px;
    }

    @media (min-width: 992px) and(max-width: 1199px) {
      -ms-flex-preferred-size: 45%;
      flex-basis: 45%;
      padding: 25px 0;
    }

    @media (min-width: 1200px) {
      width: 460px;
    }
  }

  .stats {
    @media (min-width: 992px) {
      -ms-flex-preferred-size: 29%;
      flex-basis: 29%;
      width: 297px;
    }

    @media (min-width: 992px) and(max-width: 1199px) {
      -ms-flex-preferred-size: 36%;
      flex-basis: 36%;
    }

    @media (min-width: 1200px) {
      width: 310px;
    }

    @media (max-width: 991px) {
      height: 200px !important;
    }
  }
`;

const PlayerInfoMobile = styled.div`
  padding: 35px 0 0;
  text-align: center;
  display: none;
  margin-bottom: 17px;
  display: none;
  @media (max-width: 991px) {
    display: block;
  }
`;

const PlayerInfoTitleDesktop = styled.div`
  margin-bottom: 17px;
  display: none;
  @media (min-width: 992px) {
    display: block;
  }
`;

const PlayerInfoNumber = styled.div`
  font-size: 54px;
  text-transform: uppercase;
  font-family: Montserrat, sans-serif;
  font-weight: 700;
  letter-spacing: -0.02em;
  color: #fff;
  line-height: 1em;
  display: inline-block;
  vertical-align: middle;

  @media (min-width: 992px) {
    font-size: 64px;
    margin-left: -80px;
  }

  @media (min-width: 1200px) {
    font-size: 92px;
    margin-left: -123px;
    margin-right: 5px;
  }
`;

const PlayerInfoName = styled.h1`
  display: inline-block;
  vertical-align: middle;
  font-size: 32px;
  line-height: 1em;
  color: #fff;
  margin: 0;
  @media (min-width: 992px) {
    font-size: 32px;
  }

  @media (min-width: 1200px) {
    font-size: 54px;
    line-height: 46px;
  }
`;

const PlayerInfoFirstName = styled.span`
  display: block;
  font-size: 16px;
  line-height: 1em;
  @media (min-width: 992px) {
    font-size: 18px;
  }

  @media (min-width: 1200px) {
    font-size: 26px;
  }
`;

const PlayerInfoLastName = styled.span`
  color: #ffdc11;
`;

const PlayerInfoDetails = styled.div`
  display: flex;
  flex-wrap: wrap;

  @media (max-width: 991px) {
    padding: 0 17px;
  }
`;

const PlayerInfoDetailsItem = styled.div`
  padding: 10px 0;

  &.height {
    -ms-flex-preferred-size: 33%;
    flex-basis: 33%;

    @media (min-width: 992px) {
      -ms-flex-preferred-size: 19%;
      flex-basis: 19%;
    }
  }

  &.weight {
    -ms-flex-preferred-size: 33%;
    flex-basis: 33%;

    @media (min-width: 992px) {
      -ms-flex-preferred-size: 24%;
      flex-basis: 24%;
    }
  }

  &.age {
    -ms-flex-preferred-size: 33%;
    flex-basis: 33%;

    @media (min-width: 992px) {
      -ms-flex-preferred-size: 15%;
      flex-basis: 15%;
    }
  }

  &.college {
    -ms-flex-preferred-size: 100%;
    flex-basis: 100%;

    @media (min-width: 992px) {
      -ms-flex-preferred-size: 42%;
      flex-basis: 42%;
    }
  }

  &.born {
    -ms-flex-preferred-size: 100%;
    flex-basis: 100%;

    @media (min-width: 992px) {
      -ms-flex-preferred-size: 58%;
      flex-basis: 58%;
    }
  }

  &.position {
    -ms-flex-preferred-size: 100%;
    flex-basis: 100%;

    @media (min-width: 992px) {
      -ms-flex-preferred-size: 42%;
      flex-basis: 42%;
    }
  }
`;

const PlayerInfoDetailsTitle = styled.h6`
  font-size: 10px;
  color: #9a9da2;
  line-height: 1.2em;
  margin-bottom: 0.1em;
  font-weight: 400;
`;

const PlayerInfoDetailsValue = styled.div`
  font-family: Montserrat, sans-serif;
  font-size: 15px;
  line-height: 1.2em;
  font-weight: 700;

  @media (min-width: 992px) and(max-width: 1199px) {
    font-size: 12px;
  }
`;

const PlayerInfoStats = styled.div`
  overflow: hidden;

  @media (min-width: 992px) {
    padding: 25px 0 0;
  }
`;

const PlayerInfoStatsItem = styled.div`
  float: left;
  width: 33.3%;
`;

const Circular = styled.div`
  margin: 10px 0;

  @media (min-width: 992px) {
    margin: 0;
  }
`;

const CircularBar = styled.div`
  width: 62px;
  height: 62px;

  @media (min-width: 992px) {
    display: inline-block;
    vertical-align: middle;
    margin: 0 6px 0 0;
  }

  @media (min-width: 992px) and(max-width: 1199px) {
    width: 44px;
    height: 44px;
  }
`;

const CircularPercents = styled.span`
  font-size: 12px;
  text-transform: uppercase;
  font-family: Montserrat, sans-serif;
  font-weight: 700;
  letter-spacing: -0.02em;
  display: block;
  position: absolute;
  left: 50%;
  top: 50%;
  transform: translate(-50%, -50%);
  text-align: center;
  color: #31404b;
  line-height: 1.2em;
  color: #fff;
  font-size: 15px;

  small {
    display: block;
    font-size: 8px;
    line-height: 1.2em;
    color: #9a9da2;
  }

  @media (min-width: 992px) and(max-width: 1199px) {
    font-size: 12px;
  }
`;

const CircularLabel = styled.div`
  color: #9a9da2;
  display: block;
  font-size: 8px;
  text-transform: uppercase;
  font-family: Montserrat, sans-serif;
  font-weight: 700;
  letter-spacing: -0.02em;
  line-height: 1.2em;
  text-align: center;

  @media (min-width: 992px) {
    display: inline-block;
    vertical-align: middle;
    text-align: left;
    margin: 0;
  }

  strong {
    color: #fff;
    display: block;
  }

  @media (min-width: 992px) and(max-width: 1199px) {
    font-size: 9px;
  }
`;

const data = [
  { subject: "OFF", A: 120 },
  { subject: "AST", A: 98 },
  { subject: "TCK", A: 86 },
  { subject: "TRY", A: 99 },
  { subject: "PAS", A: 85 },
  { subject: "DEF", A: 65 },
];

const PlayerHeading = () => {
  return (
    <StyledPlayerHeading>
      <div className="container mx-auto">
        <TeamLogo>
          <img src={logoLg} alt="logo" />
        </TeamLogo>

        <PlayerInfoMobile>
          <div className="player-info__number">38</div>
          <h1 className="player-info__name">
            <span className="player-info__first-name">James</span>{" "}
            <span className="player-info__last-name">Girobili</span>
          </h1>
        </PlayerInfoMobile>

        <PlayerInfo>
          <PlayerInfoItemCard>
            <PlayerInfoItemCardContent>
              <PlayerInfoItem className="photo" aria-hidden="false">
                <PlayerPhoto>
                  <img src={player} alt="" />
                </PlayerPhoto>
              </PlayerInfoItem>
              <PlayerInfoItem className="details" aria-hidden="false">
                <PlayerInfoTitleDesktop>
                  <PlayerInfoNumber>6</PlayerInfoNumber>
                  <PlayerInfoName>
                    <PlayerInfoFirstName>Tyrone</PlayerInfoFirstName>{" "}
                    <PlayerInfoLastName>Thompson</PlayerInfoLastName>
                  </PlayerInfoName>
                </PlayerInfoTitleDesktop>
                <PlayerInfoDetails>
                  <PlayerInfoDetailsItem className="height">
                    <PlayerInfoDetailsTitle>Height</PlayerInfoDetailsTitle>
                    <PlayerInfoDetailsValue>6'5"</PlayerInfoDetailsValue>
                  </PlayerInfoDetailsItem>
                  <PlayerInfoDetailsItem className="weight">
                    <PlayerInfoDetailsTitle>Weight</PlayerInfoDetailsTitle>
                    <PlayerInfoDetailsValue>111 kg</PlayerInfoDetailsValue>
                  </PlayerInfoDetailsItem>
                  <PlayerInfoDetailsItem className="age">
                    <PlayerInfoDetailsTitle>Age</PlayerInfoDetailsTitle>
                    <PlayerInfoDetailsValue>26</PlayerInfoDetailsValue>
                  </PlayerInfoDetailsItem>
                  <PlayerInfoDetailsItem className="college">
                    <PlayerInfoDetailsTitle>College</PlayerInfoDetailsTitle>
                    <PlayerInfoDetailsValue>
                      Rockbell Bay College
                    </PlayerInfoDetailsValue>
                  </PlayerInfoDetailsItem>
                  <PlayerInfoDetailsItem className="born">
                    <PlayerInfoDetailsTitle>Born</PlayerInfoDetailsTitle>
                    <PlayerInfoDetailsValue>
                      Wellington, California. NZ
                    </PlayerInfoDetailsValue>
                  </PlayerInfoDetailsItem>
                  <PlayerInfoDetailsItem className="position">
                    <PlayerInfoDetailsTitle>Position</PlayerInfoDetailsTitle>
                    <PlayerInfoDetailsValue>Prop</PlayerInfoDetailsValue>
                  </PlayerInfoDetailsItem>
                </PlayerInfoDetails>
                <PlayerDetail />
                <PlayerStats />
                <PlayerInfoStats>
                  <PlayerInfoStatsItem>
                    <CircularStat
                      value={60}
                      label={
                        <>
                          <strong>Points</strong> per game
                        </>
                      }
                    >
                      2.6<small>avg</small>
                    </CircularStat>
                  </PlayerInfoStatsItem>
                  <PlayerInfoStatsItem>
                    <CircularStat
                      value={75}
                      label={
                        <>
                          <strong>Pass</strong> per game
                        </>
                      }
                    >
                      4.5<small>avg</small>
                    </CircularStat>
                  </PlayerInfoStatsItem>
                  <PlayerInfoStatsItem>
                    <CircularStat
                      value={80}
                      label={
                        <>
                          <strong>Tackle</strong> per game
                        </>
                      }
                    >
                      6.8<small>avg</small>
                    </CircularStat>
                  </PlayerInfoStatsItem>
                </PlayerInfoStats>
              </PlayerInfoItem>
              <PlayerInfoItem className="stats" aria-hidden="false">
                {/* <ResponsiveContainer width="100%" height="100%"> */}
                {/* <RadarChart cx="50%" cy="50%" outerRadius="80%" data={data}> */}
                <RadarChart
                  cx="50%"
                  cy="50%"
                  outerRadius={120}
                  width={400}
                  height={400}
                  data={data}
                >
                  <PolarGrid />
                  <PolarAngleAxis dataKey="subject" tick={{ fill: "white" }} />
                  <Radar
                    name="Mike"
                    dataKey="A"
                    stroke="#ffdc11"
                    fill="#ffdc11"
                    fillOpacity={0.9}
                  />
                </RadarChart>
                {/* </ResponsiveContainer> */}
              </PlayerInfoItem>
            </PlayerInfoItemCardContent>
          </PlayerInfoItemCard>
        </PlayerInfo>
      </div>
    </StyledPlayerHeading>
  );
};

export default PlayerHeading;
