import styled from "styled-components";

import pageHeading from "./assets/images/page-heading.jpeg";
import pageHeadingPattern from "./assets/images/page-heading-pattern.png";
import logoLg from "./assets/images/logo-lg.png";

const Container = styled.div`
  background-color: #1e2024;
  background-image: url(${pageHeading});
  background-size: cover;
  background-repeat: no-repeat;
  background-position: 50% 50%;
  position: relative;

  &:before {
    height: 24px;
    background-image: linear-gradient(
        90deg,
        rgba(236, 240, 246, 0.15) 4px,
        transparent 0
      ),
      linear-gradient(90deg, rgba(236, 240, 246, 0.15) 4px, transparent 0),
      linear-gradient(90deg, rgba(236, 240, 246, 0.15) 4px, transparent 0),
      linear-gradient(90deg, rgba(236, 240, 246, 0.15) 4px, transparent 0),
      linear-gradient(90deg, rgba(236, 240, 246, 0.15) 4px, transparent 0),
      linear-gradient(90deg, rgba(236, 240, 246, 0.15) 4px, transparent 0),
      linear-gradient(90deg, rgba(0, 0, 0, 0.1) 4px, transparent 0),
      linear-gradient(90deg, rgba(0, 0, 0, 0.11) 4px, transparent 0);
    background-repeat: repeat-x;
    background-size: 36px 12px, 36px 15px, 36px 17px, 36px 20px, 36px 18px,
      36px 15px, 72px 15px, 108px 18px;
    background-position: 0 100%, 6px 100%, 12px 100%, 18px 100%, 24px 100%,
      30px 100%, 6px 100%, 24px 100%;
  }
  &:after,
  &:before {
    content: "";
    display: block;
    position: absolute;
    left: 0;
    right: 0;
    bottom: 0;
  }

  &:after {
    top: 0;
    z-index: 1;
    background-image: url(${pageHeadingPattern});
    background-size: auto;
    background-position: 100% 0;
    background-repeat: no-repeat;
    opacity: 0.4;
  }
  .container {
    position: relative;
    z-index: 2;
  }
`;

const TeamLogo = styled.div`
  position: absolute;
  left: -45%;
  top: -5%;
  opacity: 0.05;
  z-index: -1;
`;

const PlayerHeading = () => {
  return (
    <Container>
      <TeamLogo>
        <img src={logoLg} alt="logo" />
      </TeamLogo>

      <div className="player-info__title player-info__title--mobile">
        <div className="player-info__number">38</div>
        <h1 className="player-info__name">
          <span className="player-info__first-name">James</span>{" "}
          <span className="player-info__last-name">Girobili</span>
        </h1>
      </div>

      <div className="container"></div>
    </Container>
  );
};

export default PlayerHeading;
