import pageHeading from "./assets/images/page-heading.jpeg";
import pageHeadingPattern from "./assets/images/page-heading-pattern.png";
import logoLg from "./assets/images/logo.png";
import player from "./assets/images/tyronethompson.png";

import {
  Radar,
  RadarChart,
  Legend,
  PolarGrid,
  PolarAngleAxis,
  PolarRadiusAxis,
  ResponsiveContainer,
} from "recharts";

import CircularStat from "./CircularStat";
import PlayerDetail from "./PlayerDetails";
import PlayerStats from "./PlayerStats";
import PlayerName from "./PlayerName";
import PlayerTitle from "./PlayerTitle";
import { PlayerInfo, PlayerInfoItem, PlayerInfoItemCard, PlayerInfoItemCardContent, StyledPlayerHeading, TeamLogo } from "./styled-components";


const data = [
  { subject: "OFF", A: 120 },
  { subject: "AST", A: 98 },
  { subject: "TCK", A: 86 },
  { subject: "TRY", A: 99 },
  { subject: "PAS", A: 85 },
  { subject: "DEF", A: 65 },
];

const PlayerHeading = () => {
  return (
    <StyledPlayerHeading>
      <div className="container mx-auto">
        <TeamLogo>
          <img src={logoLg} alt="logo" />
        </TeamLogo>

        <PlayerTitle className="mobile" firstName="Tyrone" lastName="Thompson" number="6"/>

        <PlayerInfo>
          <PlayerInfoItemCard>
            <PlayerInfoItemCardContent>
              <PlayerInfoItem className="photo" aria-hidden="false">
                <PlayerPhoto>
                  <img src={player} alt="" />
                </PlayerPhoto>
              </PlayerInfoItem>
              <PlayerInfoItem className="details" aria-hidden="false">
                <PlayerTitle className="desktop" firstName="Tyrone" lastName="Thompson" number="6"/>
                <PlayerDetail />
                <PlayerStats />
              </PlayerInfoItem>
              <PlayerInfoItem className="stats" aria-hidden="false">
                {/* <ResponsiveContainer width="100%" height="100%"> */}
                {/* <RadarChart cx="50%" cy="50%" outerRadius="80%" data={data}> */}
                <RadarChart
                  cx="50%"
                  cy="50%"
                  outerRadius={120}
                  width={400}
                  height={400}
                  data={data}
                >
                  <PolarGrid />
                  <PolarAngleAxis dataKey="subject" tick={{ fill: "white" }} />
                  <Radar
                    name="Mike"
                    dataKey="A"
                    stroke="#ffdc11"
                    fill="#ffdc11"
                    fillOpacity={0.9}
                  />
                </RadarChart>
                {/* </ResponsiveContainer> */}
              </PlayerInfoItem>
            </PlayerInfoItemCardContent>
          </PlayerInfoItemCard>
        </PlayerInfo>
      </div>
    </StyledPlayerHeading>
  );
};

export default PlayerHeading;
